mapper.select:
        SELECT


--         级联表的字段
        ,fuzhuang.fuzhuang_name as fuzhuangName
        ,fuzhuang.fuzhuang_photo as fuzhuangPhoto
        ,fuzhuang.fuzhuang_types as fuzhuangTypes
        ,fuzhuang.fuzhuang_kucun_number as fuzhuangKucunNumber
        ,fuzhuang.fuzhuang_price as fuzhuangPrice
        ,fuzhuang.fuzhuang_old_money as fuzhuangOldMoney
        ,fuzhuang.fuzhuang_new_money as fuzhuangNewMoney
        ,fuzhuang.fuzhuang_clicknum as fuzhuangClicknum
        ,fuzhuang.fuzhuang_content as fuzhuangContent
        ,fuzhuang.shangxia_types as shangxiaTypes
        ,fuzhuang.fuzhuang_delete as fuzhuangDelete
        ,yonghu.yonghu_name as yonghuName
        ,yonghu.yonghu_phone as yonghuPhone
        ,yonghu.yonghu_id_number as yonghuIdNumber
        ,yonghu.yonghu_photo as yonghuPhoto
        ,yonghu.yonghu_email as yonghuEmail
        ,yonghu.yonghu_sum_jifen as yonghuSumJifen
        ,yonghu.yonghu_new_jifen as yonghuNewJifen
        ,yonghu.huiyuandengji_types as huiyuandengjiTypes

        FROM fuzhuang_commentback  a
        left JOIN fuzhuang fuzhuang ON a.fuzhuang_id = fuzhuang.id
        left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id



                and a.id in

                #{item}



                and (
                    a.fuzhuang_id = #{params.fuzhuangId}
                )


                and (
                    a.yonghu_id = #{params.yonghuId}
                )


                and a.fuzhuang_commentback_text like CONCAT('%',#{params.fuzhuangCommentbackText},'%')


                  and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


                  and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 


                and a.reply_text like CONCAT('%',#{params.replyText},'%')


                  and UNIX_TIMESTAMP(a.update_time) >= UNIX_TIMESTAMP(#{params.updateTimeStart}) 


                  and UNIX_TIMESTAMP(a.update_time) <= UNIX_TIMESTAMP(#{params.updateTimeEnd}) 




                and a.fuzhuang_id IS NOT NULL


                and fuzhuang.fuzhuang_name like CONCAT('%',#{params.fuzhuangName},'%')


                and fuzhuang.fuzhuang_types = #{params.fuzhuangTypes}



                  and fuzhuang.fuzhuang_kucun_number >= #{params.fuzhuangKucunNumberStart}   


                  and fuzhuang.fuzhuang_kucun_number <= #{params.fuzhuangKucunNumberEnd}   


                and fuzhuang.fuzhuang_kucun_number = #{params.fuzhuangKucunNumber}


                  and fuzhuang.fuzhuang_price >= #{params.fuzhuangPriceStart}   


                  and fuzhuang.fuzhuang_price <= #{params.fuzhuangPriceEnd}   


                and fuzhuang.fuzhuang_price = #{params.fuzhuangPrice}


                  and fuzhuang.fuzhuang_old_money >= #{params.fuzhuangOldMoneyStart}   


                  and fuzhuang.fuzhuang_old_money <= #{params.fuzhuangOldMoneyEnd}   


                  and fuzhuang.fuzhuang_new_money >= #{params.fuzhuangNewMoneyStart}   


                  and fuzhuang.fuzhuang_new_money <= #{params.fuzhuangNewMoneyEnd}   


                  and fuzhuang.fuzhuang_clicknum >= #{params.fuzhuangClicknumStart}   


                  and fuzhuang.fuzhuang_clicknum <= #{params.fuzhuangClicknumEnd}   


                and fuzhuang.fuzhuang_clicknum = #{params.fuzhuangClicknum}


                and fuzhuang.fuzhuang_content like CONCAT('%',#{params.fuzhuangContent},'%')


                and fuzhuang.shangxia_types = #{params.shangxiaTypes}



                  and fuzhuang.fuzhuang_delete >= #{params.fuzhuangDeleteStart}   


                  and fuzhuang.fuzhuang_delete <= #{params.fuzhuangDeleteEnd}   


                and fuzhuang.fuzhuang_delete = #{params.fuzhuangDelete}



                and a.yonghu_id IS NOT NULL


                and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')


                and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')


                and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')


                and yonghu.yonghu_email like CONCAT('%',#{params.yonghuEmail},'%')


                  and yonghu.yonghu_sum_jifen >= #{params.yonghuSumJifenStart}   


                  and yonghu.yonghu_sum_jifen <= #{params.yonghuSumJifenEnd}   


                  and yonghu.yonghu_new_jifen >= #{params.yonghuNewJifenStart}   


                  and yonghu.yonghu_new_jifen <= #{params.yonghuNewJifenEnd}   


                and yonghu.huiyuandengji_types = #{params.huiyuandengjiTypes}




        order by a.${params.orderBy} desc
