mapper.select:
        SELECT


--         级联表的字段

        FROM fuzhuang  a



                and a.id in

                #{item}



                and a.fuzhuang_name like CONCAT('%',#{params.fuzhuangName},'%')


                and a.fuzhuang_types = #{params.fuzhuangTypes}


                  and a.fuzhuang_kucun_number >= #{params.fuzhuangKucunNumberStart}   


                  and a.fuzhuang_kucun_number <= #{params.fuzhuangKucunNumberEnd}   


                and a.fuzhuang_kucun_number = #{params.fuzhuangKucunNumber}


                  and a.fuzhuang_price >= #{params.fuzhuangPriceStart}   


                  and a.fuzhuang_price <= #{params.fuzhuangPriceEnd}   


                and a.fuzhuang_price = #{params.fuzhuangPrice}


                  and a.fuzhuang_old_money >= #{params.fuzhuangOldMoneyStart}   


                  and a.fuzhuang_old_money <= #{params.fuzhuangOldMoneyEnd}   


                  and a.fuzhuang_new_money >= #{params.fuzhuangNewMoneyStart}   


                  and a.fuzhuang_new_money <= #{params.fuzhuangNewMoneyEnd}   


                  and a.fuzhuang_clicknum >= #{params.fuzhuangClicknumStart}   


                  and a.fuzhuang_clicknum <= #{params.fuzhuangClicknumEnd}   


                and a.fuzhuang_clicknum = #{params.fuzhuangClicknum}


                and a.fuzhuang_content like CONCAT('%',#{params.fuzhuangContent},'%')


                and a.shangxia_types = #{params.shangxiaTypes}


                  and a.fuzhuang_delete >= #{params.fuzhuangDeleteStart}   


                  and a.fuzhuang_delete <= #{params.fuzhuangDeleteEnd}   


                and a.fuzhuang_delete = #{params.fuzhuangDelete}




        order by a.${params.orderBy} desc
